import { Link } from 'react-router-dom';
import  { useEffect, useState } from "react";

import '../cssmodule/Header.css';

import {FaCut,FaHandHoldingUsd,FaUserAlt}  from "react-icons/fa";

import {
    connectWallet,
    getCurrentWalletConnected
    //import here
  } from "../utilities/Interact.js";


const  Header=(props)=>{

    function addWalletListener() {
        if (window.ethereum) {
          window.ethereum.on("accountsChanged", (accounts) => {
            if (accounts.length > 0) {
              setWallet(accounts[0]);
              setStatus("ðŸ‘†ðŸ½ Write a token_id in ttoken_id-field above.");
            } else {
              setWallet("");
              setStatus("ðŸ¦Š Connect to Metamask using the top right button.");
            }
          });
        }
      }

      //State variables
      const [walletAddress, setWallet] = useState("");
      const [Status, setStatus] = useState("");
    
     
      const connectWalletPressed = async () => {
        const walletResponse = await connectWallet();
        setStatus(walletResponse.Status);
        setWallet(walletResponse.address);
      };
    
      useEffect(async () => {
        const { address, status } = await getCurrentWalletConnected();
        setWallet(address)
        setStatus(status);
    
       
      
      addWalletListener();
      }, []);
    
      useEffect(()=>{
        props.waladdhandler(walletAddress);
    
      },[walletAddress])
    
      //console.log(walletAddress);


    return (
    <div class='nav-bar'>

        <Link to='/' style={{ textDecoration: 'none',marginLeft: 0 }}><h3 className='nft-logo'>NFT-CHARTER</h3></Link>

        <Link to='/mortage' style={{ textDecoration: 'none',paddingLeft: 0 }}><button class='btn' ><FaHandHoldingUsd/>     MORTAGE</button></Link> 
        <Link to='/splitter' style={{ textDecoration: 'none',marginLeft: 0 }}><button class='btn'><FaCut/>  SPLITTER</button></Link>
        
        {/* <button class='btn-r'><FaUserAlt/>  login</button>  */}

        <button class='btn-r' onClick={connectWalletPressed}>
        {walletAddress.length > 0 ? (
                "Connected: " +
                String(walletAddress).substring(0, 6) +
                "..." +
                String(walletAddress).substring(38)
              ) : (
                <span>Connect Wallet</span>
              )}</button> 



    </div>
    );
    

}


export default Header;
